const answer = sample(WORDS);
// To make debugging easier, we'll log the solution in the console.
console.info({ answer });

function Game() {
	const [guess, setGuess] = React.useState("");

	function handleSubmit(event) {
		event.preventDefault();
		setGuess("");
		console.log({ guess });
	}

	return (
		<>
			<GuessInput />
		</>
	);
}

export default Game;
